function [coor, lat] = Read_Tinker_Structure_MOL()
%to Read TINKER
%%and write CSSR file for Molden visualization
fp = fopen('output.frac');
%fs = fopen('output.cssr','w');
running = 1;
count = 1;
%disp('reading output.frac');
a = fgetl(fp);
numAtoms = str2num(a(1:6));
    a = fgetl(fp);
        optlat = str2num(a(1:73));
         % fprintf(fs,'                                    %8.3f %8.3f %8.3f\n',optlat(1:3) );
         % fprintf(fs,'                     %8.3f %8.3f %8.3f         1\n',optlat(4:6) );
         % fprintf(fs,' %3i %3i\n     generated by USPEX \n',numAtoms,0);
        optlat(4:6) = optlat(4:6)*pi/180;
        lat = latConverter(optlat);
        for i = 1 : numAtoms
           a = fgetl(fp);
           tmp = str2num(a(14:48));
           coor(count,:) = tmp;
         % fprintf(fs,' %10s %9.5f %9.5f %9.5f\n',a(4:13),tmp );
           count = count + 1;
        end
           running = 0;
fclose(fp);
%fclose(fs);

%disp(optlat)
%disp(coor)
